properties.widgets.Mariadb-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "int",
        "long",
        "float",
        "double",
        "bytes",
        "string"
      ],
      "schema-default-type": "string"
    }
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this source for lineage"}
        },
        {
          "widget-type": "textbox",
          "name": "host",
          "label": "Host",
          "widget-attributes": {"default": "localhost"}
        },
        {
          "widget-type": "number",
          "name": "port",
          "label": "Port",
          "widget-attributes": {"default": "3306"}
        },
        {
          "widget-type": "textbox",
          "name": "database",
          "label": "Database"
        },
        {
          "widget-type": "textarea",
          "name": "importQuery",
          "label": "Import Query",
          "widget-attributes": {"rows": "4"},
          "plugin-function": {
            "add-properties": [{
              "name": "query",
              "widget-type": "textarea",
              "label": "Query",
              "widget-attributes": {"rows": "4"},
              "plugin-property-for-value": "importQuery"
            }],
            "widget": "outputSchema",
            "output-property": "schema",
            "omit-properties": [{"name": "schema"}]
          }
        },
        {
          "widget-type": "textarea",
          "name": "boundingQuery",
          "label": "Bounding Query",
          "widget-attributes": {"rows": "4"}
        },
        {
          "widget-type": "textbox",
          "name": "splitBy",
          "label": "Split-By Field Name"
        },
        {
