properties.widgets.OracleDatastream-cdcSource:
{
  "outputs": [],
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "name": "usingExistingStream",
          "widget-type": "toggle",
          "label": "Use an existing Datastream stream",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "No",
              "value": "false"
            },
            "on": {
              "label": "Yes",
              "value": "true"
            }
          }
        },
        {
          "name": "region",
          "widget-type": "textbox",
          "label": "Region",
          "widget-attributes": {"default": "us-central1"}
        },
        {
          "name": "streamId",
          "widget-type": "textbox",
          "label": "Stream ID"
        },
        {
          "name": "connectivityMethod",
          "widget-type": "radio-group",
          "label": "Connectivity Method",
          "widget-attributes": {
            "layout": "inline",
            "default": "ip-allowlisting",
            "options": [
              {
                "id": "ip-allowlisting",
                "label": "IP allowlisting"
              },
              {
                "id": "forward-ssh-tunnel",
                "label": "Forward SSH Tunnel"
              },
              {
                "id": "private-connectivity",
                "label": "Private connectivity (VPC peering)"
              }
            ]
          }
        },
        {
          "name": "sshHost",
          "widget-type": "textbox",
          "label": "Host"
        },
        {
          "name": "sshPort",
          "widget-type": "textbox",
          "label": "Port",
          "widget-attributes": {"default": "22"}
        },
        {
          "name": "sshUser",
          "widget-type": "textbox",
          "label": "Username"
        },
        {
          "name": "sshAuthenticationMethod",
          "widget-type": "radio-group",
          "label": "Authentication Method",
          "widget-attributes": {
            "layout": "inline",
            "default": "private-public-key",
            "options": [
              {
                "id": "private-public-key",
                "label": "Private/Public Key Pair"
              },
              {
                "id": "password",
                "label": "Password"
              }
            ]
          }
        },
        {
          "name": "sshPassword",
          "widget-type": "password",
          "label": "Password"
        },
        {
          "name": "sshPrivateKey",
          "widget-type": "securekey-textarea",
          "label": "Private Key"
        },
        {
          "name": "privateConnectionName",
          "widget-type": "textbox",
          "label": "Private Connection Name"
        }
      ]
    },
    {
      "label": "Database Location",
      "properties": [
        {
          "name": "host",
          "widget-type": "textbox",
          "label": "Host"
        },
        {
          "name": "port",
          "widget-type": "textbox",
          "label": "Port",
          "widget-attributes": {"default": "1521"}
        },
        {
          "name": "sid",
          "widget-type": "textbox",
          "label": "System Identifier (SID)",
          "widget-attributes": {"default": "ORCL"}
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "name": "user",
          "widget-type": "textbox",
          "label": "Username"
        },
        {
          "name": "password",
          "widget-type": "password",
          "label": "Password"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "name": "replicateExistingData",
          "widget-type": "toggle",
          "label": "Replicate Existing Data",
          "widget-attributes": {
            "default": "true",
            "off": {
              "label": "No",
              "value": "false"
            },
            "on": {
              "label": "Yes",
              "value": "true"
            }
          }
        },
        {
          "name": "project",
          "widget-type": "textbox",
          "label": "Project",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "name": "dsServiceAccountKey",
          "widget-type": "securekey-textarea",
          "label": "Datastream Service Account Key",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "name": "gcsServiceAccountKey",
          "widget-type": "securekey-textarea",
          "label": "GCS Service Account Key",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "name": "gcsBucket",
          "widget-type": "textbox",
          "label": "GCS Bucket"
        },
        {
          "name": "gcsPathPrefix",
          "widget-type": "textbox",
          "label": "Path Prefix"
        }
      ]
    }
  ],
