  "filters": [
    {
      "condition": {"expression": "usingExistingStream == true"},
      "name": "showExistingStream",
      "show": [{
        "name": "streamId",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "usingExistingStream == false"},
      "name": "showNewStream",
      "show": [
        {
          "name": "Database Location",
          "type": "group"
        },
        {
          "name": "gcsPathPrefix",
          "type": "property"
        },
        {
          "name": "connectivityMethod",
          "type": "property"
        },
        {
          "name": "Credentials",
          "type": "group"
        },
        {
          "name": "gcsBucket",
          "type": "property"
        },
        {
          "name": "replicateExistingData",
          "type": "property"
        }
      ]
    },
    {
      "condition": {"expression": "usingExistingStream == false && connectivityMethod == 'forward-ssh-tunnel'"},
      "name": "showSSH",
      "show": [
        {
          "name": "sshHost",
          "type": "property"
        },
        {
          "name": "sshPort",
          "type": "property"
        },
        {
          "name": "sshAuthenticationMethod",
          "type": "property"
        },
        {
          "name": "sshUser",
          "type": "property"
        }
      ]
    },
    {
      "condition": {"expression": "sshAuthenticationMethod == 'password' && connectivityMethod == 'forward-ssh-tunnel' && usingExistingStream == false"},
      "name": "showSSHPassword",
      "show": [{
        "name": "sshPassword",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "sshAuthenticationMethod == 'private-public-key' && connectivityMethod == 'forward-ssh-tunnel' && usingExistingStream == false"},
      "name": "showSSHPrivateKey",
      "show": [{
        "name": "sshPrivateKey",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "connectivityMethod == 'private-connectivity' && usingExistingStream == false"},
      "name": "showPrivateConnectionName",
      "show": [{
        "name": "privateConnectionName",
        "type": "property"
      }]
    }
  ]
}
