properties.widgets.MultiFieldAdder-transform:
{
  "outputs": [],
  "metadata": {"spec-version": "1.0"},
  "configuration-groups": [{
    "label": "Multi Field Adder Configuration",
    "properties": [{
      "name": "fieldValue",
      "widget-type": "keyvalue",
      "label": "Field and Value Pairs",
      "widget-attributes": {
        "kv-delimiter": ":",
        "delimiter": ","
      },
      "plugin-function": {
        "widget": "outputSchema",
        "missing-required-fields-message": "No field value specified.",
        "method": "POST",
        "required-fields": ["fieldValue"],
        "output-property": "schema",
        "plugin-method": "outputSchema"
      }
    }]
  }]
}

