properties.widgets.FastFilter-transform:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "label": "schema"
  }],
  "metadata": {"spec-version": "1.0"},
  "configuration-groups": [{
    "label": "Fast Filter",
    "properties": [
      {
        "widget-type": "input-field-selector",
        "name": "sourceField",
        "label": "Source Field",
        "widget-attributes": {"width": "small"}
      },
      {
        "widget-type": "select",
        "name": "operator",
        "label": "Operator",
        "widget-attributes": {
          "default": "contains",
          "values": [
            "=",
            "!=",
            ">",
            ">=",
            "<",
            "<=",
            "contains",
            "does not contain",
            "starts with",
            "ends with",
            "does not start with",
            "does not end with",
            "matches regex",
            "does not match regex"
          ]
        }
      },
      {
        "widget-type": "textbox",
        "name": "criteria",
        "label": "Criteria",
        "widget-attributes": {"width": "small"}
      },
      {
        "widget-type": "select",
        "name": "shouldIgnoreCase",
        "label": "Ignore Case?",
        "widget-attributes": {
          "default": "false",
          "values": [
            "true",
            "false"
          ]
        }
      }
    ]
  }]
