properties.widgets.HTTPToHDFS-action:
{
  "outputs": [],
  "metadata": {"spec-version": "1.0"},
  "configuration-groups": [{
    "label": "HTTP To HDFS Action Properties",
    "properties": [
      {
        "widget-type": "textbox",
        "name": "url",
        "label": "URL"
      },
      {
        "widget-type": "textbox",
        "name": "hdfsFilePath",
        "label": "HDFS File Path"
      },
      {
        "widget-type": "select",
        "name": "method",
        "label": "HTTP Method",
        "widget-attributes": {
          "default": "GET",
          "values": [
            "GET",
            "POST"
          ]
        }
      },
      {
        "widget-type": "textbox",
        "name": "body",
        "label": "Request Body"
      },
      {
        "widget-type": "keyvalue",
        "name": "requestHeaders",
        "label": "Request Headers",
        "widget-attributes": {
          "kv-delimiter": ":",
          "delimiter": "\n",
          "showDelimiter": "false"
        }
      },
      {
        "widget-type": "select",
        "name": "outputFormat",
        "label": "Output File Format",
        "widget-attributes": {
          "default": "Text",
          "values": [
            "Text",
            "Binary"
          ]
        }
      },
      {
        "widget-type": "select",
        "name": "charset",
        "label": "Charset for Text",
        "widget-attributes": {
          "default": "UTF-8",
          "values": [
            "UTF-8",
            "US-ASCII",
            "ISO-8859-1",
            "UTF-16",
            "UTF-16BE",
            "UTF-16LE"
          ]
        }
      },
      {
        "widget-type": "select",
        "name": "followRedirects",
        "label": "Should Follow Redirects ?",
        "widget-attributes": {
          "default": "true",
          "values": [
            "true",
            "false"
          ]
        }
      },
      {
        "widget-type": "select",
        "name": "disableSSLValidation",
        "label": "Disable SSL Validation",
        "widget-attributes": {
          "default": "true",
          "values": [
            "true",
            "false"
          ]
        }
      },
      {
        "widget-type": "select",
        "name": "numRetries",
        "label": "Number of Retries",
        "widget-attributes": {
          "default": "3",
          "values": [
            "3",
            "0",
            "1",
            "2",
            "4",
            "5",
            "6",
            "7",
            "8",
            "9",
            "10"
          ]
        }
      },
      {
        "widget-type": "number",
        "name": "connectTimeout",
        "label": "Connection Timeout (milliseconds)",
        "widget-attributes": {"default": "60000"}
      },
      {
        "widget-type": "number",
        "name": "readTimeout",
        "label": "Read Timeout (milliseconds)",
        "widget-attributes": {"default": "60000"}
      },
      {
        "widget-type": "textbox",
        "name": "outputPath",
        "label": "Token Key for HDFS File Path",
        "widget-attributes": {"default": "filePath"}
      },
      {
        "widget-type": "textbox",
        "name": "responseHeaders",
        "label": "Token Key for Response Headers",
        "widget-attributes": {"default": "responseHeaders"}
      }
    ]
  }]
}
