properties.widgets.Cassandra-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "int",
        "long",
        "float",
        "double",
        "bytes",
        "string"
      ],
      "schema-default-type": "string"
    }
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Cassandra Configuration",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name"
        },
        {
          "widget-type": "textbox",
          "name": "initialAddress",
          "label": "Initial Address"
        },
        {
          "widget-type": "textbox",
          "name": "port",
          "label": "Port"
        },
        {
          "widget-type": "textbox",
          "name": "username",
          "label": "Username"
        },
        {
          "widget-type": "password",
          "name": "password",
          "label": "Password"
        },
        {
          "widget-type": "keyvalue",
          "name": "properties",
          "label": "Other Properties",
          "widget-attributes": {"showDelimiter": "false"}
        }
      ]
    },
    {
      "label": "Keyspace and Column Family",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "keyspace",
          "label": "Keyspace"
        },
        {
          "widget-type": "textbox",
          "name": "partitioner",
          "label": "Partitioner"
        },
        {
          "widget-type": "textbox",
          "name": "columnFamily",
          "label": "Column Family"
        },
        {
          "widget-type": "textbox",
          "name": "query",
          "label": "CQL Query"
        }
      ]
    }
  ],
  "display-name": "Cassandra"
}
