properties.widgets.CreateRecord-transform:
{
  "metadata": {
    "spec-version": "1.5",
    "label": "Create Record"
  },
  "configuration-groups": [{
    "label": "Create Hierarchy Configuration",
    "properties": [
      {
        "name": "fieldMapping",
        "widget-type": "textbox",
        "label": "Mapping"
      },
      {
        "name": "includeNonMappedFields",
        "widget-type": "toggle",
        "label": "Include fields missing in mapping",
        "widget-attributes": {
          "default": "off",
          "off": {"label": "No"},
          "on": {"label": "Yes"}
        }
      }
    ]
  }],
  "display-name": "Create Record"
}
