properties.widgets.Pivot-batchaggregator:
{
  "outputs": [],
  "metadata": {
    "spec-version": "1.1",
    "label": "Pivot"
  },
  "configuration-groups": [
    {
      "label": "Pivot settings",
      "properties": [
        {
          "widget-type": "keyvalue",
          "name": "pivotColumns",
          "label": "Pivot Columns",
          "widget-attributes": {
            "kv-delimiter": "=",
            "delimiter": ";",
            "showDelimiter": "false"
          }
        },
        {
          "widget-type": "input-field-selector",
          "name": "pivotRow",
          "label": "Pivot Rows"
        },
        {
          "widget-type": "function-dropdown-with-alias",
          "name": "aggregates",
          "label": "Aggregates",
          "widget-attributes": {
            "dropdownOptions": [
              "Avg",
              "Count",
              "First",
              "Last",
              "Max",
              "Min",
              {
                "label": "Standard Deviation",
                "value": "Stddev"
              },
              "Sum",
              "Variance",
              {
                "label": "Collect List",
                "value": "CollectList"
              },
              {
                "label": "Collect Set",
                "value": "CollectSet"
              },
              {
                "label": "Count Distinct",
                "value": "CountDistinct"
              },
              {
                "label": "Longest String",
                "value": "LongestString"
              },
              {
                "label": "Shortest String",
                "value": "ShortestString"
              },
              {
                "label": "Number of Nulls",
                "value": "CountNulls"
              },
              {
                "label": "Concat",
                "value": "Concat"
              },
              {
                "label": "Concat Distinct",
                "value": "ConcatDistinct"
              },
              {
                "label": "Logical AND",
                "value": "LogicalAnd"
              },
              {
                "label": "Logical OR",
                "value": "LogicalOr"
              },
              {
                "label": "Sum of squares",
                "value": "SumOfSquares"
              },
              {
                "label": "Corrected sum of squares",
                "value": "CorrectedSumOfSquares"
              }
            ],
            "placeholders": {
              "field": "field",
              "alias": "alias"
            }
          }
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "numPartitions",
          "label": "Number of Partitions"
        },
        {
          "widget-type": "textbox",
          "name": "defaultValue",
          "label": "Default Value",
          "widget-attributes": {"placeholder": "The default value to use in case a cell does not contain a value after pivoting. Defaults to null"}
        },
        {
          "widget-type": "keyvalue",
          "name": "fieldAliases",
          "label": "Field Aliases",
          "widget-attributes": {
            "kv-delimiter": ":",
            "delimiter": ",",
            "showDelimiter": "false"
          }
        }
      ]
    },
    {
      "label": "Error Handling",
      "properties": [{
        "name": "on-error",
        "widget-type": "radio-group",
        "label": "On Error",
        "widget-attributes": {
          "layout": "block",
          "default": "skip-error",
          "options": [
            {
              "id": "skip-error",
              "label": "Skip error"
            },
            {
              "id": "send-to-error-port",
              "label": "Send to error port"
            },
            {
              "id": "fail-pipeline",
              "label": "Fail pipeline"
            }
          ]
        }
      }]
    }
  ],
  "emit-errors": true,
  "emit-alerts": true,
