properties.widgets.Sampling-batchaggregator:
{
  "outputs": [],
  "metadata": {"spec-version": "1.3"},
  "configuration-groups": [{
    "label": "Sampling Plugin",
    "properties": [
      {
        "widget-type": "textbox",
        "name": "sampleSize",
        "label": "Sample Size"
      },
      {
        "widget-type": "textbox",
        "name": "samplePercentage",
        "label": "Sample Percentage"
      },
      {
        "widget-type": "select",
        "name": "samplingType",
        "label": "Sampling Type",
        "widget-attributes": {
          "default": "Systematic",
          "values": [
            "Systematic",
            "Reservoir"
          ]
        }
      },
      {
        "widget-type": "textbox",
        "name": "overSamplingPercentage",
        "label": "Over Sampling Percentage"
      },
      {
        "widget-type": "textbox",
        "name": "random",
        "label": "Random"
      },
      {
        "widget-type": "textbox",
        "name": "totalRecords",
        "label": "Total Records"
      }
    ]
  }]
}
