properties.widgets.Wrangler-transform:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "label": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "int",
        "long",
        "float",
        "double",
        "bytes",
        "string",
        "timestamp",
        "array",
        "enum",
        "record",
        "map",
        "union"
      ],
      "schema-default-type": "string",
      "property-watch": "format"
    }
  }],
  "metadata": {"spec-version": "1.6"},
  "configuration-groups": [
    {
      "label": "Input Selection and Prefilters",
      "properties": [
        {
          "widget-type": "hidden",
          "name": "workspace"
        },
        {
          "widget-type": "textbox",
          "name": "field",
          "label": "Input field name",
          "widget-attributes": {"default": "*"}
        },
        {
          "widget-type": "textbox",
          "name": "precondition",
          "label": "Precondition",
          "widget-attributes": {"default": "false"}
        }
      ]
    },
    {
      "label": "Directives",
      "properties": [
        {
          "widget-type": "wrangler-directives",
          "name": "directives",
          "label": "Recipe",
          "widget-attributes": {"placeholder": "#pragma load-directives my-directive; my-directive :body;"}
        },
        {
          "widget-type": "csv",
          "name": "udd",
          "label": "User Defined Directives(UDD)"
        }
      ]
    },
    {
      "label": "Error Handling",
      "properties": [{
        "name": "on-error",
        "widget-type": "radio-group",
        "label": "On Error",
        "widget-attributes": {
          "layout": "block",
          "default": "fail-pipeline",
          "options": [
            {
              "id": "skip-error",
              "label": "Skip error"
            },
            {
              "id": "send-to-error-port",
              "label": "Send to error port"
            },
            {
              "id": "fail-pipeline",
              "label": "Fail pipeline"
            }
          ]
        }
      }]
    }
  ],
  "emit-errors": true,
  "emit-alerts": true
}
